import React, { Component } from "react";
import "./styles.css";

export default class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      estaLogado: false,
      mensagensNovas: 0
    };
  }

  handleLoginLogout = () => {
    const novoEstaLogado = !this.state.estaLogado;
    this.setState({ estaLogado: novoEstaLogado });
  };

  render() {
    const paginaLogada = (
      <div>
        <h1>Bem vindo(a) de volta!</h1>
        {this.state.mensagensNovas > 0 && (
          <p>VocÃªs tem {this.state.mensagensNovas} mensagens novas.</p>
        )}
        <button>Feed</button>
        <button>Lista de Amigos</button>
        <button onClick={this.handleLoginLogout}>Sair</button>
      </div>
    );

    const paginaDeslogada = (
      <div>
        <h1>Por favor, logue-se</h1>
        <button onClick={this.handleLoginLogout}>Login</button>
      </div>
    );

    return (
      <div>
        <p>Sistema Maneiro de Login</p>
        {this.state.estaLogado ? paginaLogada : paginaDeslogada}
      </div>
    );
  }
}
