import React from 'react';
import styled from 'styled-components';


const MainContainerInput = styled.div`
    display: flex;
    justify-content: center;
    flex-direction: column;    
`

const ContainerFiltro = styled.div`
    display: flex;
    justify-content: center;
    padding: 10px;
`
const ContainerInput = styled.div`
    display: flex;
    justify-content: center;
    padding: 10px;
`




class InputTarefas extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            novaTarefa: "",
        }
    }

    escreverNovaTarefa = (event) => {
        this.setState({
            novaTarefa: event.target.value
        })
    }

    imprimeTarefa = () => {
        // if (this.state.novaTarefa !== ""){
        //     this.adicionaTarefa
        // }
        
    }





    render = () =>{
        console.log(this.adicionaTarefa)
        return(
            <MainContainerInput>
                <h2>Lista de tarefas</h2>
                <ContainerInput>                    
                    <input type="text" value={this.state.novaTarefa} onChange={this.escreverNovaTarefa}></input>
                    <button onClick={this.imprimeTarefa}>Adicionar</button>
                </ContainerInput>
                <ContainerFiltro>
                    <label>Filtro</label>
                    <select>
                        <option>Nenhum</option>
                        <option>Pendentes</option>
                        <option>Completas</option>
                    </select>
                </ContainerFiltro>
            </MainContainerInput>            
        )
    }
}

export default InputTarefas;
