import React from 'react';
import axios from 'axios';

const baseUrl = "https://api.lyrics.ovh/v1/artist/music"

class Letras extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            letraAtual:"",
            artistaAtual: "",
            musicaAtual: "",
        }
    }

    capturaArtista = (event) => {
        const novoArtista = event.target.value;
        this.setState ({artistaAtual:novoArtista})
    }

    

    render(){
        return(
            <div>
                <input type="text" placeholder="Nome do artista" value={this.state.artistaAtual} onChange={capturaArtista} />
                <br/>
                <br/>
                <input type="text" placeholder="Nome da música" />
                <button>Procurar letra da música</button>
                {this.state.artistaAtual && this.state.musicaAtual &&
                    <p>{this.state.letraAtual}</p>
                }
            </div>
        )
    }
}

export default Letras;
